import { useRef, useState } from "react";
import classes from "./Checkout.module.css";

const isEmpty = value => value.trim() === '';
const isNotFive = value => value.trim().lenth !== 5;

const Checkout = (props) => {
    const [formInputValidty, setFormInputValidty] = useState({
        name: true,
        street: true,
        city: true,
        postalCode: true,
    });

    const nameInputRef = useRef();
    const streetInputRef = useRef();
    const postalInputRef = useRef();
    const cityInputRef = useRef();

    const confirmHandler = event => {
        event.preventDefault();
        const enterdName = nameInputRef.current.value; 
        const enterdStreet = streetInputRef.current.value;
        const enterdPostal = postalInputRef.current.value;
        const enterdCity = cityInputRef.current.value;


        const enterdNameIsValid = !isEmpty(enterdName);
        const enterdStreetIsValid = !isEmpty(enterdStreet);
        const enterdCityIsValid = !isEmpty(enterdCity);
        const enterdPostalIsValid = !isNotFive(enterdPostal);

        setFormInputValidty({
            name: enterdNameIsValid,
            street: enterdStreetIsValid,
            city: enterdCityIsValid,
            postalCode: enterdPostalIsValid,
        })


        const formIsValid = enterdNameIsValid && enterdStreetIsValid && enterdCityIsValid && enterdPostalIsValid;
        if(!formIsValid) {
            return;
        }

        props.onConfirm({
            name: enterdName,
            street: enterdStreet,
            city: enterdCity,
            postalCode: enterdPostal,
        })
    };


    const nameControlClasses = `${classes.control} ${formInputValidty.name? '' : classes.invalid} `;
    const streetControlClasses = `${classes.control} ${formInputValidty.street? '' : classes.invalid} `;
    const postalControlClasses = `${classes.control} ${formInputValidty.postalCode? '' : classes.invalid} `;
    const cityControlClasses = `${classes.control} ${formInputValidty.city? '' : classes.invalid} `;

    return (
        <form className={classes.form} onSubmit={confirmHandler}>
          <div className={nameControlClasses}>
            <label htmlFor='name'>Your Name</label>
            <input type='text' id='name' ref={nameInputRef} />
            {!formInputValidty.name && <p>Please enter a valid name</p> }
          </div>
          <div className={streetControlClasses}>
            <label htmlFor='street'>Street</label>
            <input type='text' id='street' ref={streetInputRef} />
            {!formInputValidty.street && <p>Please enter a valid street</p> }
          </div>
          <div className={postalControlClasses}>
            <label htmlFor='postal'>Postal Code</label>
            <input type='text' id='postal' ref={postalInputRef} />
            {!formInputValidty.postalCode && <p>Please enter a valid postal code</p> }
          </div>
          <div className={cityControlClasses}>
            <label htmlFor='city'>City</label>
            <input type='text' id='city' ref={cityInputRef} />
            {!formInputValidty.city && <p>Please enter a valid city</p> }
          </div>
          <div className={classes.actions}>
            <button type='button' onClick={props.onCancel}>
              Cancel
            </button>
            <button className={classes.submit}>Confirm</button>
          </div>
        </form>
      );
    };


export default Checkout;
