import React, { useEffect } from "react";
import classes from "./AvilableMeals.module.css";
import Card from "../UI/Card";
import MealItem from "./MealItem/MealItem";

const AvilableMeals = () => {
  useEffect(() => {
    fetch("https://react-http-1afd1-default-rtdb.firebaseio.com/meals.json");
  }, []);

  const mealsList = DUMMY_MEALS.map(function (meal) {
    return (
      <MealItem
        key={meal.id}
        id={meal.id}
        name={meal.name}
        description={meal.description}
        price={meal.price}
      />
    );
  });
  return (
    <section className={classes.meals}>
      <Card>
        <ul>{mealsList}</ul>
      </Card>
    </section>
  );
};

export default AvilableMeals;
